sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.imports
 */

function evaluateProperty({
    api
}) {
    if (!api.data.glide_form_1.nowRecordFormBlob) {} else {
        if (api.data.glide_form_1.nowRecordFormBlob && api.data.glide_form_1.nowRecordFormBlob.fields && api.data.request_details_internationalization_support_1 && api.data.request_details_internationalization_support_1.output) {
            var request_form_fields = api.data.glide_form_1.nowRecordFormBlob.fields;
            request_form_fields.due_date.label = api.data.request_details_internationalization_support_1.output['request_details_form_due_date'];
            request_form_fields.description.label = api.data.request_details_internationalization_support_1.output['request_details_form_description'];
            request_form_fields.reviewer.label = api.data.request_details_internationalization_support_1.output['request_details_form_reviewer'];
            return request_form_fields;
        } else{
            return '';
        }
    }
}
